import React, {useState, useEffect} from "react";
function App() {
    const [data, setData] = useState([]);
    const [toggle, setToggle] = useState(false);
    const [sort, setSort] = useState(false);
    useEffect(() => {
        let list = [];
        for (let i = 0; i < 100; i++) {
            list.push(getRandomNumber(5, 500));
        }
        setData(list);
        if (sort) {
            setData(data.sort((a, b) => a - b));
        }
    }, [toggle]);
    function bubbleSort() {
        setSort(true);
    }
    return (
        <main>
            {
                data.map((data, index) => {
                    return (
                        <div className="line" key={index} style={{height: data + "px"}}>

                        </div>
                    )
                })
            }
            <button onClick={() => setToggle(!toggle)}>Generate</button>
            <button onClick={bubbleSort}>Sort</button>
        </main>
    )
}
const getRandomNumber = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1) + min);
}
export default App;
