import React, {useState, useEffect} from "react";
function App() {
    const [data, setData] = useState([]);
    const [toggle, setToggle] = useState(false);
    useEffect(() => {
        let list = [];
        for (let i = 0; i < 100; i++) {
            list.push(getRandomNumber(5, 500));
        }
        setData(list);
    }, [toggle]);
    const bubbleSort = async (array) => {
        for (let i = 0; i < array.length; i++) {
            for (let j = i + 1; j < array.length; j++) {
                if (array[i] > array[j]) {
                    await Promise.all([
                        swap(i, j, array)
                    ])
                }
            }
        }
        setData(array);
    }
    const swap = async (i, j, array) => {
        const temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
    return (
        <main>
            {
                data.map((data, index) => {
                    return (
                        <div className="line" key={index} style={{height: data + "px"}}>

                        </div>
                    )
                })
            }
            <button onClick={() => setToggle(!toggle)}>Generate</button>
            <button onClick={() => bubbleSort(data)}>Sort</button>
        </main>
    )
}
const getRandomNumber = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1) + min);
}
export default App;
