import React from "react";
import { bubbleSort } from "./sortingAlgorithms/bubbleSort";
import { insertionSort } from "./sortingAlgorithms/insertionSort";
import { mergeSort } from "./sortingAlgorithms/mergeSort";
import { quickSort } from "./sortingAlgorithms/quickSort";
import { selectionSort } from "./sortingAlgorithms/selectionSort";
const {useState, useEffect} = React;
const first = "red";
const second = "turquoise";
function App() {
    const elements = [100, 200, 300, 400];
    const [algorithms, setAlgorithms] = useState(0);
    const algos = ["Bubble Sort", "Insertion Sort", "Selection Sort", "Quick Sort", "Merge Sort"];
    const [algo, setAlgo] = useState(false);
    const [alert, setAlert] = useState(0);
    const [showSpeed, setShowSpeed] = useState(false);
    const [element, setElement] = useState(false);
    const [ele, setEle] = useState(1);
    const [array, setArray] = useState([]);
    const [speed, setSpeed] = useState(3);
    const speeds = [0.25, 0.5, 0.75, 1, 1.25, 1.5, 1.75, 2];
    useEffect(() => {
        let list = [];
        for (let i = 0; i <= elements[ele]; i++) {
            list.push(getRandom(5, 700));
        }
        setArray(list);
    }, [ele]);
    function bubbleSortVisualizer() {
        const animations = bubbleSort(array);
        const arrayBars = document.getElementsByClassName("bar");
        for (let i = 0; i < animations.length; i++) {
            const colorChange = i % 4 <= 1;
            if (colorChange) {
              const [barOneIdx, barTwoIdx] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              const barTwoStyle = arrayBars[barTwoIdx].style;
              const color = i % 4 === 0 ? first : second;
              setTimeout(() => {
                barOneStyle.backgroundColor = color;
                barTwoStyle.backgroundColor = color;
              }, i * speeds[speed]);
            } else {
              setTimeout(() => {
                const [barOneIdx, newHeight] = animations[i];
                const barOneStyle = arrayBars[barOneIdx].style;
                barOneStyle.height = `${newHeight}px`;
              }, i * speeds[speed]);
            }
          }
    }
    function insertionSortVisualizer() {
        const animations = insertionSort(array);
        const arrayBars = document.getElementsByClassName('bar');
        for (let i = 0; i < animations.length; i++) {
            const colorChange = i % 4 <= 1;
            if (colorChange) {
              const [barOneIdx, barTwoIdx] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              const barTwoStyle = arrayBars[barTwoIdx].style;
              const color = i % 4 === 0 ? first : second;
              setTimeout(() => {
                barOneStyle.backgroundColor = color;
                barTwoStyle.backgroundColor = color;
              }, i * speeds[speed]);
            } else {
              setTimeout(() => {
                const [barOneIdx, newHeight] = animations[i];
                const barOneStyle = arrayBars[barOneIdx].style;
                barOneStyle.height = `${newHeight}px`;
              }, i * speeds[speed]);
            }
          }
    }
    function selectionSortVisualizer() {
        const animations = selectionSort(array);
        const arrayBars = document.getElementsByClassName("bar");
        for (let i = 0; i < animations.length; i++) {
            const colorChange = i % 4 <= 1;
            if (colorChange) {
              const [barOneIdx, barTwoIdx] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              const barTwoStyle = arrayBars[barTwoIdx].style;
              const color = i % 4 === 0 ? first : second;
              setTimeout(() => {
                barOneStyle.backgroundColor = color;
                barTwoStyle.backgroundColor = color;
              }, i * speeds[speed]);
            } else {
              setTimeout(() => {
                const [barOneIdx, newHeight] = animations[i];
                const barOneStyle = arrayBars[barOneIdx].style;
                barOneStyle.height = `${newHeight}px`;
              }, i * speeds[speed]);
            }
          }
    }
    function quickSortVisualizer() {
        const animations = quickSort(array);
        const arrayBars = document.getElementsByClassName("bar");
        for (let i = 0; i < animations.length; i++) {
            const colorChange = i % 4 <= 1;
            if (colorChange) {
              const [barOneIdx, barTwoIdx] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              const barTwoStyle = arrayBars[barTwoIdx].style;
              const color = i % 4 === 0 ? first : second;
              setTimeout(() => {
                barOneStyle.backgroundColor = color;
                barTwoStyle.backgroundColor = color;
              }, i * speeds[speed]);
            } else {
              setTimeout(() => {
                const [barOneIdx, newHeight] = animations[i];
                const barOneStyle = arrayBars[barOneIdx].style;
                barOneStyle.height = `${newHeight}px`;
              }, i * speeds[speed]);
            }
        }
    }
    function mergeSortVisualizer() {
        const animations = mergeSort(array);
        const arrayBars = document.getElementsByClassName("bar");
        for (let i = 0; i < animations.length; i++) {
            const colorChange = i % 3 !== 2;
            if (colorChange) {
              const [barOneIdx, barTwoIdx] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              const barTwoStyle = arrayBars[barTwoIdx].style;
              const color = i % 3 === 0 ? first : second;
              setTimeout(() => {
                barOneStyle.backgroundColor = color;
                barTwoStyle.backgroundColor = color;
              }, i * speeds[speed]);
            } else {
              setTimeout(() => {
                const [barOneIdx, newHeight] = animations[i];
                const barOneStyle = arrayBars[barOneIdx].style;
                barOneStyle.height = `${newHeight}px`;
              }, i * speeds[speed]);
            }
        }
    }
    function sorting() {
        quickSortVisualizer()
    }
    return (
        <main>
            <div className="close" onClick={() => {
                setAlgo(false);
                setShowSpeed(false);
                setElement(false);
            }} style={{display: algo || showSpeed || element ? "block" : "none"}}></div>
            <header>
                <div className="logo" onClick={() => window.location.href = "/"}>
                    <div className="sorting">
                        <div className="line-1"></div>
                        <div className="line-2"></div>
                        <div className="line-3"></div>
                    </div>
                    <p>Sorting Visualizer</p>
                </div>
                <div className="options">
                    <div className="algorithms">
