export function bubbleSort(array) {
    const animations = [];
    if (array.length <= 1) return;
    const arrayHelper = array.slice();
    bubbleSortHelper(arrayHelper, animations);
    return animations;
}
function bubbleSortHelper(arrayHelper, animations) {
    const length = arrayHelper.length;
    for (let i = 0; i < length - 1; i++) {
        for (let j = 0; j < length - i - 1; j++) {
            animations.push([j, j + 1]);
            animations.push([j, j + 1]);
            if (arrayHelper[j] > arrayHelper[j + 1]) {
                animations.push([j, arrayHelper[j + 1]]);
                animations.push([j + 1, arrayHelper[j]]);
                swap(j, j + 1, arrayHelper);
            } else {
                animations.push([-1, -1]);
                animations.push([-1, -1]);
            }
        }
    }
}
function swap(i, j, arrayHelper) {
    const temp = arrayHelper[i];
    arrayHelper[i] = arrayHelper[j];
    arrayHelper[j] = temp;
}
