import React, {useState, useEffect} from "react";
function App() {
    const [data, setData] = useState([]);
    const [toggle, setToggle] = useState(false);
    useEffect(() => {
        let list = [];
        for (let i = 0; i < 100; i++) {
            list.push(getRandomNumber(5, 500));
        }
        setData(list);
    }, [toggle]);
    function quickSort(array) {
        quickSortStarter(array, 0, array.length - 1);
        return array;
    }
    function quickSortStarter(array, startIdx, endIdx) {
        if (startIdx >= endIdx) return;
        let keyIdx = startIdx;
        let leftIdx = startIdx + 1;
        let rightIdx = endIdx;
        while (rightIdx >= leftIdx) {
            if (array[rightIdx] < array[keyIdx] && array[leftIdx] > array[keyIdx]) {
                swap(rightIdx, leftIdx, array);
            }
            if (array[rightIdx] >= array[keyIdx]) rightIdx -= 1;
            if (array[leftIdx] <= array[keyIdx]) leftIdx += 1;
        } swap(rightIdx, keyIdx, array);
        const leftArrayUnsorted = rightIdx - 1 < endIdx - (rightIdx + 1);
        if (leftArrayUnsorted) {
            quickSortStarter(array, startIdx, rightIdx - 1);
            quickSortStarter(array, rightIdx + 1, endIdx);
        } else {
            quickSortStarter(array, rightIdx + 1, endIdx);
            quickSortStarter(array, startIdx, rightIdx - 1);
        }
    }
    const swap = async (i, j, array) => {
        const temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
    return (
        <main>
            {
                data.map((data, index) => {
                    return (
                        <div className="line" key={index} style={{height: data + "px"}}>

                        </div>
                    )
                })
            }
            <button onClick={() => setToggle(!toggle)}>Generate</button>
            <button onClick={() => sorting(data)}>Sort</button>
        </main>
    )
}
const getRandomNumber = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1) + min);
}
export default App;
