export function selectionSort(array) {
    const animations = [];
    const arrayHelper = array.slice();
    selectionSortHelper(arrayHelper, animations);
    return animations;
}
function selectionSortHelper(arrayHelper, animations) {
    for (let i = 0; i < arrayHelper.length - 1; i++) {
        let smallest = arrayHelper[i];
        let smallestIdx = i;
        for (let j = i + 1; j < arrayHelper.length; j++) {
            animations.push([smallestIdx, j]);
            animations.push([smallestIdx, j]);
            animations.push([0, arrayHelper[0]]);
            animations.push([0, arrayHelper[0]]);
            if (arrayHelper[j] < smallest) {
                smallest = arrayHelper[j];
                smallestIdx = j;
            }
        }
        animations.push([i, smallestIdx]);
        animations.push([i, smallestIdx]);
        animations.push([i, smallest]);
        animations.push([smallestIdx, arrayHelper[i]]);
        swap(smallestIdx, i, arrayHelper);
    }
}
