export function insertionSort(array) {
    const animations = [];
    const arrayHelper = array.slice();
    insertionSortHelper(arrayHelper, animations);
    return animations;
}
function insertionSortHelper(arrayHelper, animations) {
    for (let i = 1; i < arrayHelper.length; i++) {
        let j = i;
        animations.push([i, j]);
        animations.push([i, j]);
        while (j > 0 && arrayHelper[j] < arrayHelper[j - 1]) {
            swap(j, j - 1, arrayHelper);
            j -= 1;
        }
    }
}
function swap(i, j, arrayHelper) {
    const temp = arrayHelper[i];
    arrayHelper[i] = arrayHelper[j];
    arrayHelper[j] = temp;
}
