export function bubbleSort(array) {
    const animations = [];
    const arrayHelper = array.slice();
    bubbleSortHelper(arrayHelper, animations);
    array = arrayHelper;
    return [animations, array];
}
function bubbleSortHelper(arrayHelper, animations) {
    // const length = arrayHelper.length;
    // for (let i = 0; i < length - 1; i++) {
