    let isSorted = false;
    let counter = 0;
    while (!isSorted) {
        isSorted = true;
        for (let i = 0; i < array.length - 1 - counter; i++) {
            animations.push([i, i + 1]);
            animations.push([i, i + 1]);
            if (array[i] > array[i + 1]) {
                animations.push([i, arrayHelper[i + 1]]);
                animations.push([i + 1, arrayHelper[i]]);
                swap(i, i + 1, array);
                isSorted = false;
            }
        }
        counter += 1;
    }
    return array;
}
function swap(i, j, arrayHelper) {
    const temp = arrayHelper[i];
    arrayHelper[i] = arrayHelper[j];
    arrayHelper[j] = temp;
}
