export function selectionSort(array) {
    const animations = [];
    const arrayHelper = array.slice();
    selectionSortHelper(arrayHelper, animations);
    return animations;
}
function selectionSortHelper(arrayHelper, animations) {
    let startIdx = 0;
    while (startIdx < arrayHelper.length - 1) {
        let smallestIdx = startIdx;
        for (let i = startIdx + 1; i < arrayHelper.length; i++) {
            if (arrayHelper[smallestIdx] > arrayHelper[i]) {
                animations.push([smallestIdx, i]);
                animations.push([smallestIdx, i]);
                smallestIdx = i;
            }
        }
        animations.push([smallestIdx, startIdx]);
        animations.push([smallestIdx, startIdx]);
        swap(startIdx, smallestIdx, arrayHelper);
        startIdx += 1;
    }
}
function swap(i, j, arrayHelper) {
    const temp = arrayHelper[i];
    arrayHelper[i] = arrayHelper[j];
    arrayHelper[j] = temp;
}
