import React from "react";
import { bubbleSort } from "./sortingAlgorithms/bubbleSort";
const {useState, useEffect} = React;
function App() {
    const [bars, setBars] = useState(50);
    const [speed, setSpeed] = useState(1);
    const [array, setArray] = useState([]);
    useEffect(() => {
        let list = [];
        for (let i = 10; i < bars; i++) {
            list.push(getRandom(20, 200));
        }
        setArray(list);
    }, []);
    function bubbleSortVisualizer() {
        const [animations, randomValue] = bubbleSort(array);
        for (let i = 0; i < animations.length; i++) {
            const colorInfos = i % 4 === 0 || i % 4 === 1;
            const bar = document.querySelector(".bar");
            if (colorInfos) {
                const color = i % 4 === 0 ? "red" : "pink";
                const [barOneIdx, barTwoIdx] = animations[i];
                const barOneStyle = bar[barOneIdx].style;
                const barTwoStyle = bar[barTwoIdx].style;
                setTimeout(() => {
                    barOneStyle.backgroundColor = color;
                    barTwoStyle.backgroundColor = color;
                }, i * speed);
            }
        }
    }
    return (
        <main>
            <header>
                {
                    array.map((data, index) => {
                        return (
                            <div key={index} className="bar" style={{height: data + "px"}}>
                            </div>
                        )
                    })
                }
            </header>
            <button onClick={bubbleSortVisualizer}>Bubble Sort</button>
        </main>
    )
}
function getRandom(min, max) {
    return Math.floor(Math.random() * (max - min + 1) + min);
}
export default App;
