import React from "react";
import { bubbleSort } from "./sortingAlgorithms/bubbleSort";
import { insertionSort } from "./sortingAlgorithms/insertionSort";
import { mergeSort } from "./sortingAlgorithms/mergeSort";
import { quickSort } from "./sortingAlgorithms/quickSort";
import { selectionSort } from "./sortingAlgorithms/selectionSort";
const {useState, useEffect} = React;
const first = "red";
const second = "turquoise";
function App() {
    const [bars, setBars] = useState(200);
    const [array, setArray] = useState([]);
    const [reset, setReset] = useState(false);
    const [speed, setSpeed] = useState(0);
    const speeds = [0.25, 0.5, 0.75, 1, 1.25, 1.5, 1.75, 2];
    useEffect(() => {
        let list = [];
        for (let i = 0; i < bars; i++) {
            list.push(getRandom(5, 700));
        }
        setArray(list);
    }, [reset]);
    function bubbleSortVisualizer() {
        const animations = bubbleSort(array);
        const arrayBars = document.getElementsByClassName("bar");
        for (let i = 0; i < animations.length; i++) {
            const colorChange = i % 4 <= 1;
            if (colorChange) {
              const [barOneIdx, barTwoIdx] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              const barTwoStyle = arrayBars[barTwoIdx].style;
              const color = i % 4 === 0 ? first : second;
              setTimeout(() => {
                barOneStyle.backgroundColor = color;
                barTwoStyle.backgroundColor = color;
              }, i * speeds[speed]);
            } else {
              setTimeout(() => {
                const [barOneIdx, newHeight] = animations[i];
                const barOneStyle = arrayBars[barOneIdx].style;
                barOneStyle.height = `${newHeight}px`;
              }, i * speeds[speed]);
            }
          }
    }
    function insertionSortVisualizer() {
        const animations = insertionSort(array);
        const arrayBars = document.getElementsByClassName('bar');
        for (let i = 0; i < animations.length; i++) {
            const colorChange = i % 4 <= 1;
            if (colorChange) {
              const [barOneIdx, barTwoIdx] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              const barTwoStyle = arrayBars[barTwoIdx].style;
              const color = i % 4 === 0 ? first : second;
              setTimeout(() => {
                barOneStyle.backgroundColor = color;
                barTwoStyle.backgroundColor = color;
              }, i * speeds[speed]);
            } else {
              setTimeout(() => {
                const [barOneIdx, newHeight] = animations[i];
                const barOneStyle = arrayBars[barOneIdx].style;
                barOneStyle.height = `${newHeight}px`;
              }, i * speeds[speed]);
            }
          }
    }
    function selectionSortVisualizer() {
        const animations = selectionSort(array);
        const arrayBars = document.getElementsByClassName("bar");
        for (let i = 0; i < animations.length; i++) {
            const colorChange = i % 4 <= 1;
            if (colorChange) {
              const [barOneIdx, barTwoIdx] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              const barTwoStyle = arrayBars[barTwoIdx].style;
              const color = i % 4 === 0 ? first : second;
              setTimeout(() => {
                barOneStyle.backgroundColor = color;
                barTwoStyle.backgroundColor = color;
              }, i * speeds[speed]);
            } else {
              setTimeout(() => {
                const [barOneIdx, newHeight] = animations[i];
                const barOneStyle = arrayBars[barOneIdx].style;
                barOneStyle.height = `${newHeight}px`;
              }, i * speeds[speed]);
            }
          }
    }
    function quickSortVisualizer() {
        const animations = quickSort(array);
        const arrayBars = document.getElementsByClassName("bar");
        for (let i = 0; i < animations.length; i++) {
            const colorChange = i % 4 <= 1;
            if (colorChange) {
              const [barOneIdx, barTwoIdx] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              const barTwoStyle = arrayBars[barTwoIdx].style;
              const color = i % 4 === 0 ? first : second;
              setTimeout(() => {
                barOneStyle.backgroundColor = color;
                barTwoStyle.backgroundColor = color;
              }, i * speeds[speed]);
            } else {
              setTimeout(() => {
                const [barOneIdx, newHeight] = animations[i];
                const barOneStyle = arrayBars[barOneIdx].style;
                barOneStyle.height = `${newHeight}px`;
              }, i * speeds[speed]);
            }
        }
    }
    function mergeSortVisualizer() {
        const animations = mergeSort(array);
        const arrayBars = document.getElementsByClassName("bar");
        for (let i = 0; i < animations.length; i++) {
            const colorChange = i % 3 !== 2;
            if (colorChange) {
              const [barOneIdx, barTwoIdx] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              const barTwoStyle = arrayBars[barTwoIdx].style;
              const color = i % 3 === 0 ? first : second;
              setTimeout(() => {
                barOneStyle.backgroundColor = color;
                barTwoStyle.backgroundColor = color;
              }, i * speeds[speed]);
            } else {
              setTimeout(() => {
                const [barOneIdx, newHeight] = animations[i];
                const barOneStyle = arrayBars[barOneIdx].style;
                barOneStyle.height = `${newHeight}px`;
              }, i * speeds[speed]);
            }
        }
    }
    const [algorithms, setAlgorithms] = useState(0);
    const algos = ["Bubble Sort", "Selection Sort", "Insertion Sort", "Quick Sort", "Merge Sort"];
    const [algo, setAlgo] = useState(false);
    const [alert, setAlert] = useState(0);
    const [showSpeed, setShowSpeed] = useState(false);
    return (
        <main>
            <div className="close" onClick={() => setAlgo(!algo)} style={{display: algo ? "block" : "none"}}></div>
            <header>
                <div className="logo" onClick={() => window.location.href = "/"}>
                    <div className="sorting">
                        <div className="line-1"></div>
                        <div className="line-2"></div>
                        <div className="line-3"></div>
                    </div>
                    <p>Sorting Visualizer</p>
                </div>
                <div className="options">
                    <div className="algorithms">
                        <button onMouseOver={() => setAlert(1)} onMouseLeave={() => setAlert(0)} onClick={() => setAlgo(!algo)}>{algos[algorithms]}<svg xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 292.362 292.362"><path d="M286.935 69.377c-3.614-3.617-7.898-5.424-12.848-5.424H18.274c-4.952 0-9.233 1.807-12.85 5.424C1.807 72.998 0 77.279 0 82.228c0 4.948 1.807 9.229 5.424 12.847l127.907 127.907c3.621 3.617 7.902 5.428 12.85 5.428s9.233-1.811 12.847-5.428L286.935 95.074c3.613-3.617 5.427-7.898 5.427-12.847 0-4.948-1.814-9.229-5.427-12.85z"></path></svg></button>
                        <div className={algo ? "algorithms-drop show" : "algorithms-drop"}>
                            <button onClick={() => {
                                setAlgorithms(0);
                                setAlgo(false);
                            }}>Bubble Sort</button>
                            <button onClick={() => {
                                setAlgorithms(1);
                                setAlgo(false);
                            }}>Insertion Sort</button>
                            <button onClick={() => {
                                setAlgorithms(2);
                                setAlgo(false);
                            }}>Selection Sort</button>
                            <button onClick={() => {
                                setAlgorithms(3);
                                setAlgo(false);
                            }}>Quick Sort</button>
                            <button onClick={() => {
                                setAlgorithms(4);
                                setAlgo(false);
                            }}>Merge Sort</button>
                        </div>
                        <div className="alert" style={{display: alert === 1 ? algo ? "none" : "flex" : "none"}}>
                            <div className="square"></div>
                            <p>Algorithms</p>
                        </div>
